  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'index-rediscloud',
	title: 'Create Database using Redis Enterprise Cloud',
	sidebar_label: 'Redis Enterprise Cloud',
	slug: '/create/cloud/rediscloud'
};
export const metadata = {
  "unversionedId": "create/cloud/rediscloud/index-rediscloud",
  "id": "create/cloud/rediscloud/index-rediscloud",
  "isDocsHomePage": false,
  "title": "Create Database using Redis Enterprise Cloud",
  "description": "Redis Enterprise Cloud is a fully managed cloud service by Redis Labs.Built for modern distributed applications, Redis Enterprise Cloud enables you to run any query, simple or complex, at sub-millisecond performance at virtually infinite scale without worrying about operational complexity or service availability. With modern probabilistic data structures and extensible data models, including Search, JSON, Graph, and Time Series, you can rely on Redis as your data-platform for all your real-time needs.",
  "source": "@site/docs/create/cloud/rediscloud/index-recloud.mdx",
  "slug": "/create/cloud/rediscloud",
  "permalink": "/create/cloud/rediscloud",
  "editUrl": "https://github.com/redis-developer/redis-developer/edit/master/docs/create/cloud/rediscloud/index-recloud.mdx",
  "version": "current",
  "sidebar_label": "Redis Enterprise Cloud"
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Step 1. Create free cloud account',
  id: 'step-1-create-free-cloud-account',
  children: []
}, {
  value: 'Step 2. Add subscription',
  id: 'step-2-add-subscription',
  children: []
}, {
  value: 'Step 3.  Select cloud provider',
  id: 'step-3--select-cloud-provider',
  children: []
}, {
  value: 'Step 4. Create database',
  id: 'step-4-create-database',
  children: []
}, {
  value: 'Step 5. Launch database',
  id: 'step-5-launch-database',
  children: []
}, {
  value: 'Next Step',
  id: 'next-step',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Redis Enterprise Cloud is a fully managed cloud service by Redis Labs.Built for modern distributed applications, Redis Enterprise Cloud enables you to run any query, simple or complex, at sub-millisecond performance at virtually infinite scale without worrying about operational complexity or service availability. With modern probabilistic data structures and extensible data models, including Search, JSON, Graph, and Time Series, you can rely on Redis as your data-platform for all your real-time needs.`}</p>
    <h3 {...{
      "id": "step-1-create-free-cloud-account"
    }}>{`Step 1. Create free cloud account`}</h3>
    <p>{`Create your free `}<a href="https://redislabs.com/try-free/" target="_blank" rel="noopener">{`Redis Enterprise Cloud account`}</a>{`. Once you click on “Get Started”, you will receive an email with a link to activate your account and complete your signup process.`}</p>
    <h3 {...{
      "id": "step-2-add-subscription"
    }}>{`Step 2. Add subscription`}</h3>
    <p>{`Next, you will have to add  Redis Enterprise Cloud subscription. In the Redis Enterprise Cloud menu, click Subscriptions. At the bottom of the page, click the “+” sign.`}</p>
    <p><img alt={"My Image"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./images/subscription.png").default} /></p>
    <h3 {...{
      "id": "step-3--select-cloud-provider"
    }}>{`Step 3.  Select cloud provider`}</h3>
    <p>{`For the cloud provider, select Microsoft Azure and Fixed Size Plan(for demo purpose)`}</p>
    <p><img alt={"My Image"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./images/recloud1.png").default} /></p>
    <h3 {...{
      "id": "step-4-create-database"
    }}>{`Step 4. Create database`}</h3>
    <p>{`Click Create. After you create a subscription, you can create a database:`}</p>
    <p><img alt={"My Image"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./images/recloud2.png").default} /></p>
    <h3 {...{
      "id": "step-5-launch-database"
    }}>{`Step 5. Launch database`}</h3>
    <p>{`Click "Activate" and wait for few seconds till it gets activated. Once fully activated, you will see the database endpoints as shown below:`}</p>
    <p><img alt={"My Image"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./images/recloud3.png").default} /></p>
    <h3 {...{
      "id": "next-step"
    }}>{`Next Step`}</h3>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/explore/redisinsight/"
        }}>{`Connecting to the database using RedisInsight`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/howtos/moviesdatabase/getting-started/"
        }}>{`How to list & search Movies database using Redisearch`}</a></li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
