  value: 'Step 3. Create database',
  id: 'step-3-create-database',
  children: []
}, {
  value: 'Step 4. Click &quot;Activate&quot;',
  id: 'step-4-click-activate',
  children: []
}, {
  value: 'Next Step',
  id: 'next-step',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Redis Enterprise Cloud on AWS is a fully Managed Redis Enterprise as a service. Designed for modern distributed applications, Redis Enterprise Cloud on AWS is known for its high performance, infinite scalability and true high availability.`}</p>
    <p>{`Follow the below steps to setup Redis Enterprise Cloud hosted over AWS Cloud:`}</p>
    <h3 {...{
      "id": "step-1-getting-started"
    }}>{`Step 1. Getting Started`}</h3>
    <p>{`Follow this `}<a parentName="p" {...{
        "href": "https://redislabs.com/try-free/"
      }}>{`link to register`}</a>{` `}</p>
    <p><img alt={"AWS Cloud"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./tryfree.png").default} /></p>
    <h3 {...{
      "id": "step-2-choose-aws-cloud"
    }}>{`Step 2. Choose AWS Cloud`}</h3>
    <p>{`For the cloud provider, select Amazon AWS and choose Free plan.`}</p>
    <p><img alt={"AWS Cloud"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./aws3.png").default} /></p>
    <h3 {...{
      "id": "step-3-create-database"
    }}>{`Step 3. Create database`}</h3>
    <p><img alt={"AWS Cloud"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./aws4.png").default} /></p>
    <h3 {...{
      "id": "step-4-click-activate"
    }}>{`Step 4. Click "Activate"`}</h3>
    <p><img alt={"AWS Cloud"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./aws6.png").default} /></p>
    <h3 {...{
      "id": "next-step"
    }}>{`Next Step`}</h3>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/explore/redisinsight/"
        }}>{`Connecting to the database using RedisInsight`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/howtos/moviesdatabase/getting-started/"
        }}>{`How to list & search Movies database using Redisearch`}</a></li>
    </ul>
    </MDXLayout>;
}

;
