  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'index-jsonindex-document',
	title: 'How to index JSON documents using RedisJSON & RediSearch',
	sidebar_label: 'How to index JSON documents using RedisJSON & RediSearch',
	slug: '/howtos/redisjson/jsonindex-document'
};
export const metadata = {
  "unversionedId": "howtos/redisjson/json-using-redisearch/jsonindex-document/index-jsonindex-document",
  "id": "howtos/redisjson/json-using-redisearch/jsonindex-document/index-jsonindex-document",
  "isDocsHomePage": false,
  "title": "How to index JSON documents using RedisJSON & RediSearch",
  "description": "RedisJSON 2.0 Private Preview was announced for the first time during RedisConf 2021. With this newer version, RedisJSON will fully support JSONPath expressions and Active-Active geo-distribution. The Active-Active implementation is based on Conflict-free Replicated Data-Types (CRDT).",
  "source": "@site/docs/howtos/redisjson/json-using-redisearch/jsonindex-document/index-jsonindex-document.mdx",
  "slug": "/howtos/redisjson/jsonindex-document",
