export const toc = [{
  value: 'Prerequisite:',
  id: 'prerequisite',
  children: []
}, {
  value: 'Step 1. Run the &quot;preview&quot; tagged Redismod container',
  id: 'step-1-run-the-preview-tagged-redismod-container',
  children: []
}, {
  value: 'Step 2. Create an Index',
  id: 'step-2-create-an-index',
  children: []
}, {
  value: 'Step 3. Populate the database with JSON document',
  id: 'step-3-populate-the-database-with-json-document',
  children: []
}, {
  value: 'Step 4. Indexing the database with JSON document',
  id: 'step-4-indexing-the-database-with-json-document',
  children: []
}, {
  value: 'Step 5. Projecting using JSON Path expressions',
  id: 'step-5-projecting-using-json-path-expressions',
