import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
import useBaseUrl from '@docusaurus/useBaseUrl';
import RedisCard from '@site/src/theme/RedisCard';
export const toc = [{
  value: 'Getting Started',
  id: 'getting-started',
  children: [{
    value: 'Step 1. Install the pre-requisites',
    id: 'step-1-install-the-pre-requisites',
    children: []
  }, {
    value: 'Step 2. Install and compile hiredis',
    id: 'step-2-install-and-compile-hiredis',
    children: []
  }, {
    value: 'Step 3. Copy the below C code:',
    id: 'step-3-copy-the-below-c-code',
    children: []
  }, {
    value: 'Step 4. Compile the code',
    id: 'step-4-compile-the-code',
    children: []
  }, {
    value: 'Step 5. Test the code',
    id: 'step-5-test-the-code',
    children: []
