import useBaseUrl from '@docusaurus/useBaseUrl';
export const toc = [{
  value: 'Learning Objectives',
  id: 'learning-objectives',
  children: []
}, {
  value: 'Software Prerequisites',
  id: 'software-prerequisites',
  children: []
}, {
  value: 'Let&#39;s Learn Together',
  id: 'lets-learn-together',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <div class="text--center">
      <iframe width="560" height="315" src="https://www.youtube.com/embed/1t7uqAwG-DY" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
    </div>
    <p>{`Welcome to the Node.js Redis Crash Course.  I'm `}<a parentName="p" {...{
        "href": "https://twitter.com/simon_prickett"
      }}>{`Simon Prickett`}</a>{`, Developer Advocacy Manager at Redis.  `}</p>
    <p>{`In this course, you'll learn about using Redis with Node.js through a blend of video and text-based training.  You can also get hands-on with some optional workshop exercises where you'll add new functionality to an existing Node.js application.  `}</p>
    <h2 {...{
      "id": "learning-objectives"
    }}>{`Learning Objectives`}</h2>
    <p>{`By following the materials in this course, you can expect to learn about:`}</p>
    <ul>
      <li parentName="ul">{`What Redis is and where to find it.`}</li>
      <li parentName="ul">{`The benefits of adding Redis to your stack, when and how to use it.`}</li>
      <li parentName="ul">{`How to leverage Redis in the context of a modern Node.js Express application with the ioredis client.`}</li>
      <li parentName="ul">{`Using RedisInsight to browse and manage a Redis instance.`}</li>
      <li parentName="ul">{`Modeling domain objects with Redis Hashes, querying them with RediSearch.`}</li>
      <li parentName="ul">{`Storing and querying document data with the RedisJSON module.`}</li>
      <li parentName="ul">{`Asynchronous messaging and fast data ingestion with Redis Streams.`}</li>
      <li parentName="ul">{`Speeding up response times with Redis caching and Express middleware.`}</li>
      <li parentName="ul">{`Scaling your application using Redis as a session store for Express.`}</li>
    </ul>
    <h2 {...{
      "id": "software-prerequisites"
    }}>{`Software Prerequisites`}</h2>
    <p>{`To get the most from this course, you'll need a machine that can run Node.js applications, plus Docker as we'll be using a container that includes Redis and the required add-on modules for this course.`}</p>
    <p>{`You'll need the following installed on your machine:`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.docker.com/"
        }}>{`Docker`}</a>{` (you'll need the docker-compose command)`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://nodejs.org/"
        }}>{`Node.js`}</a>{` (use the current Long Term Stable - LTS - version)`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://git-scm.com/downloads"
        }}>{`git command line tools`}</a></li>
      <li parentName="ul">{`Your favorite IDE (we like `}<a parentName="li" {...{
          "href": "https://code.visualstudio.com/"
        }}>{`VSCode`}</a>{`, but anything you're comfortable with works)`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.postman.com/"
        }}>{`Postman`}</a>{` - we're going to make some API calls and Postman makes that easy.`}</li>
    </ul>
    <h2 {...{
      "id": "lets-learn-together"
    }}>{`Let's Learn Together`}</h2>
    <p>{`Sign up for the `}<a parentName="p" {...{
        "href": "https://discord.gg/redis"
      }}>{`Redis Discord`}</a>{` where you can ask me anything about the course!`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
