  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'index-redisearch',
	title: 'RediSearch',
	sidebar_label: 'RediSearch',
	slug: '/modules/redisearch'
};
export const metadata = {
  "unversionedId": "modules/redisearch/index-redisearch",
  "id": "modules/redisearch/index-redisearch",
  "isDocsHomePage": false,
  "title": "RediSearch",
  "description": "RediSearch is a powerful text search and secondary indexing engine, built on top of Redis as a Redis module. Written in C, RediSearch is extremely fast compared to other open-source search engines. It implements multiple data types and commands that fundamentally change what you can do with Redis. RediSearch supports capabilities for search and filtering such as geo-spatial queries, retrieving only IDs (instead of whole documents), and custom document scoring. Aggregations can combine map, filter, and reduce/group-by operations in custom pipelines that run across millions of elements in an instant.",
  "source": "@site/docs/modules/redisearch/index-redisearch.mdx",
  "slug": "/modules/redisearch",
  "permalink": "/modules/redisearch",
  "editUrl": "https://github.com/redis-developer/redis-developer/edit/master/docs/modules/redisearch/index-redisearch.mdx",
