export const toc = [{
  value: 'Step 1. Register and subscribe',
  id: 'step-1-register-and-subscribe',
  children: []
}, {
  value: 'Step 2. Create a database with Redisearch Module',
  id: 'step-2-create-a-database-with-redisearch-module',
  children: []
}, {
  value: 'Step 3. Connect to a database',
  id: 'step-3-connect-to-a-database',
  children: []
}, {
  value: 'Step 4. Getting Started with Redisearch',
  id: 'step-4-getting-started-with-redisearch',
  children: []
