export const toc = [{
  value: 'Step 1. Install the prerequisites',
  id: 'step-1-install-the-prerequisites',
  children: []
}, {
  value: 'Step 2. Create Redis Enterprise Cloud database',
  id: 'step-2-create-redis-enterprise-cloud-database',
