export const toc = [{
  value: 'Step 1. Installing RedisGears',
  id: 'step-1-installing-redisgears',
  children: []
}, {
  value: 'Step 2. Verifying if RedisGears module is enabled:',
  id: 'step-2-verifying-if-redisgears-module-is-enabled',
  children: []
}, {
  value: 'Step 3. Create a &quot;wordcount&quot; Python script',
  id: 'step-3-create-a-wordcount-python-script',
  children: []
}, {
  value: 'Step 4. Execute the CLI',
