export const toc = [{
  value: 'Step 1. Cloning the repository',
  id: 'step-1-cloning-the-repository',
  children: []
}, {
  value: 'Step 2. Running the application',
  id: 'step-2-running-the-application',
  children: []
}, {
  value: 'Step 3. Accessing Grafana dashboard',
  id: 'step-3-accessing-grafana-dashboard',
  children: []
