  /* @jsxRuntime classic */
/* @jsx mdx */
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
import useBaseUrl from '@docusaurus/useBaseUrl';
import RedisCard from '@site/src/theme/RedisCard';
export const toc = [{
  value: 'Pre-requisite',
  id: 'pre-requisite',
  children: []
}, {
  value: 'Step 1: Click on “Setup”',
  id: 'step-1-click-on-setup',
  children: []
}, {
  value: 'Step 2:  Enter your preferred FQDN',
  id: 'step-2--enter-your-preferred-fqdn',
  children: []
}, {
  value: 'Step 3: Enter the admin credentials',
  id: 'step-3-enter-the-admin-credentials',
  children: []
}, {
  value: 'Step 4:  Create a Database:',
  id: 'step-4--create-a-database',
  children: []
}, {
  value: 'Step 5: Connecting using redis-cli',
  id: 'step-5-connecting-using-redis-cli',
  children: []
}, {
  value: 'Pre-requisite',
  id: 'pre-requisite-1',
  children: []
}, {
  value: 'Step 1: Run the Redis container',
  id: 'step-1-run-the-redis-container',
  children: []
}, {
  value: 'Step 2: Verify if Redis container is running or not:',
  id: 'step-2-verify-if-redis-container-is-running-or-not',
  children: []
}, {
  value: 'Step 3: Connect to your database',
  id: 'step-3-connect-to-your-database',
  children: []
}, {
  value: 'Step 4: Testing Redis container',
  id: 'step-4-testing-redis-container',
  children: []
}, {
  value: 'Step 5: Running Redis container with Persistent Storage',
  id: 'step-5-running-redis-container-with-persistent-storage',
  children: []
}, {
  value: 'Next Step',
