export const toc = [{
  value: 'Step 1: Install Homebrew',
  id: 'step-1-install-homebrew',
  children: []
}, {
  value: 'Step 2: Install Redis using Homebrew',
  id: 'step-2-install-redis-using-homebrew',
  children: []
