import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
import useBaseUrl from '@docusaurus/useBaseUrl';
import RedisCard from '@site/src/theme/RedisCard';
export const toc = [{
  value: 'Getting Started',
  id: 'getting-started',
  children: [{
    value: 'Redis Launchpad',
    id: 'redis-launchpad',
    children: []
  }, {
    value: 'Technical Articles &amp; Whitepapers',
    id: 'technical-articles--whitepapers',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`Find tutorials, examples and technical articles that will help you to develop with Redis and Golang.`}</p>
    <h2 {...{
      "id": "getting-started"
    }}>{`Getting Started`}</h2>
    <p>{`Golang community has built many client libraries that you can find `}<a parentName="p" {...{
        "href": "https://redis.io/clients#go"
      }}>{`here`}</a>{`.
For your first steps with Golang and Redis, this article will show how to use the recommended library: `}<a parentName="p" {...{
        "href": "https://github.com/gomodule/redigo"
      }}>{`redigo`}</a>{`.`}</p>
 <Tabs defaultValue="redigo" values={[{
      label: 'Redigo',
      value: 'redigo'
    }, {
      label: 'Go-Redis',
      value: 'go-redis'
    }]} mdxType="Tabs">
  <TabItem value="redigo" mdxType="TabItem">
        <p>{`The `}<inlineCode parentName="p">{`redigo`}</inlineCode>{` library is located in the `}<inlineCode parentName="p">{`https://github.com/gomodule/redigo`}</inlineCode>{` that you must import in your application.`}</p>
        <h4 {...{
          "id": "step-1-import-the-redigo-module"
        }}>{`Step 1. Import the `}<inlineCode parentName="h4">{`redigo`}</inlineCode>{` module`}</h4>
        <pre><code parentName="pre" {...{
            "className": "language-bash"
          }}>{` go get github.com/gomodule/redigo/redis
`}</code></pre>
        <pre><code parentName="pre" {...{
            "className": "language-go"
          }}>{` import (
    "fmt"
    "context"
    "github.com/gomodule/redigo/redis"
 )
`}</code></pre>
        <h4 {...{
          "id": "step-2-create-a-connection-pool"
        }}>{`Step 2. Create a connection pool`}</h4>
        <pre><code parentName="pre" {...{
            "className": "language-go"
          }}>{` func newPool() *redis.Pool {
     return &redis.Pool{
        MaxIdle: 80,
        MaxActive: 12000,
        Dial: func() (redis.Conn, error) {
              c, err := redis.Dial("tcp", ":6379")
              if err != nil {
                  panic(err.Error())
              }
              return c, err
          },
      }
  }
`}</code></pre>
        <h4 {...{
          "id": "step-3-write-your-application-code"
        }}>{`Step 3. Write your application code`}</h4>
        <pre><code parentName="pre" {...{
            "className": "language-go"
          }}>{` package main

 import (
     "fmt"

     "github.com/gomodule/redigo/redis"
 )

 var pool = newPool()

 func main() {

     client := pool.Get()
     defer client.Close()

     _, err := client.Do("SET", "mykey", "Hello from redigo!")
     if err != nil {
         panic(err)
     }

     value, err := client.Do("GET", "mykey")
     if err != nil {
         panic(err)
     }

     fmt.Printf("%s \\n", value)

     _, err = client.Do("ZADD", "vehicles", 4, "car")
     if err != nil {
         panic(err)
     }
     _, err = client.Do("ZADD", "vehicles", 2, "bike")
     if err != nil {
         panic(err)
     }

     vehicles, err := client.Do("ZRANGE", "vehicles", 0, -1, "WITHSCORES")
     if err != nil {
         panic(err)
     }  
     fmt.Printf("%s \\n", vehicles)


 }

 func newPool() *redis.Pool {
     return &redis.Pool{
         MaxIdle: 80,
         MaxActive: 12000,
         Dial: func() (redis.Conn, error) {
             c, err := redis.Dial("tcp", ":6379")
             if err != nil {
                 panic(err.Error())
             }
             return c, err
         },
     }
 }    
`}</code></pre>
        <p>{`Find more information about Golang & Redis connections in the "`}<a parentName="p" {...{
            "href": "https://github.com/redis-developer/redis-connect/tree/master/golang/redigo"
          }}>{`Redis Connect`}</a>{`".`}</p>
  </TabItem>
      <TabItem value="go-redis" mdxType="TabItem">
        <p>{`Go-redis is a type-safe, Redis client library for Go with support for features like Pub/Sub, sentinel, and pipelining.It is a Redis client able to support a Redis cluster and is designed to store and update slot info automatically with a cluster change. Below are the attractive features of Go-redis:`}</p>
        <ul>
          <li parentName="ul">{`Go-redis has pooling capabilities.(Pools allow you to safely handle go-routines, auto reconnect if any error occurs)`}</li>
          <li parentName="ul">{`It supports both standard, OSS cluster AIP, and Sentinel`}</li>
          <li parentName="ul">{`Comes with Auto reconnects / Auto-rediscovers cluster slots on error/migration`}</li>
          <li parentName="ul">{`Support instrumentations`}</li>
          <li parentName="ul">{`Allows for a custom dialer (this is useful for Enterprise)`}</li>
        </ul>
        <p>{`The go-redis library is located in the `}<a parentName="p" {...{
            "href": "https://github.com/go-redis/redis"
          }}>{`https://github.com/go-redis/redis`}</a>{` that you must import in your application.`}</p>
        <h4 {...{
          "id": "step-1-run-the-redis-docker-container"
        }}>{`Step 1. Run the Redis Docker container`}</h4>
        <pre><code parentName="pre" {...{
            "className": "language-bash"
          }}>{` docker run -d -p 6379:6379 redislabs/redismod
`}</code></pre>
        <h4 {...{
          "id": "step-2-initialise-the-go-module"
        }}>{`Step 2. Initialise the Go Module`}</h4>
        <pre><code parentName="pre" {...{
            "className": "language-bash"
          }}>{` go mod init github.com/my/repo
`}</code></pre>
        <h4 {...{
          "id": "step-3-install-redisv8"
        }}>{`Step 3. Install redis/v8`}</h4>
        <pre><code parentName="pre" {...{
            "className": "language-bash"
          }}>{` go get github.com/go-redis/redis/v8
`}</code></pre>
        <h4 {...{
          "id": "step-4-create-a-maingo-file"
        }}>{`Step 4. Create a main.go file`}</h4>
        <p>{`Let us create a main.go file and write the following code to check for your connection`}</p>
        <pre><code parentName="pre" {...{
            "className": "language-bash"
          }}>{` package main

import (
    "fmt"
    "github.com/go-redis/redis"
)

func main() {
    fmt.Println("Testing Go-Redis")

    client := redis.NewClient(&redis.Options{
        Addr: "localhost:6379",
        Password: "",
        DB: 0,
    })

    pong, err := client.Ping().Result()
    fmt.Println(pong, err)

 }
`}</code></pre>
        <h4 {...{
          "id": "step-5-begin-the-compilation"
        }}>{`Step 5. Begin the compilation`}</h4>
        <pre><code parentName="pre" {...{}}>{` go run main.go
`}</code></pre>
        <p>{`By now, the Go application should successfully connect to the Redis instance and  return a successful PONG response.`}</p>
 </TabItem>
    </Tabs>
    <h3 {...{
      "id": "redis-launchpad"
    }}>{`Redis Launchpad`}</h3>
    <p>{`Redis Launchpad is like an “App Store” for Redis sample apps. You can easily find apps for your preferred frameworks and languages.
Below are few of these apps. `}<a parentName="p" {...{
        "href": "https://launchpad.redislabs.com"
      }}>{`Click here to access the complete lists of apps`}</a></p>
    <div class="row text--center">
      <div class="col ">
        <div className="ri-container">
          <h4 {...{
            "id": "rate-limiting-app-in-go"
          }}>{`Rate-Limiting app in Go`}</h4>
          <p><img alt={"launchpad"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./images/ratelimitinggo.png").default} /></p>
          <p><a parentName="p" {...{
              "href": "http://launchpad.redis.com/?id=project%3Abasic-redis-rate-limiting-demo-go-lang"
            }}>{`Rate Limiting app`}</a>{` built in Go`}</p>
        </div>
      </div>
      <div class="col">
        <div className="ri-container">
          <h4 {...{
            "id": "leaderboard-app-in-go"
          }}>{`Leaderboard app in Go`}</h4>
          <p><img alt={"launchpad"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./images/leaderboardgo.png").default} /></p>
          <p><a parentName="p" {...{
              "href": "https://launchpad.redis.com/?id=project%3Abasic-redis-leaderboard-demo-python"
            }}>{`How to implement leaderboard app`}</a>{` in Go`}</p>
        </div>
      </div>
    </div>
    <h3 {...{
      "id": "technical-articles--whitepapers"
    }}>{`Technical Articles & Whitepapers`}</h3>
    <p><strong parentName="p"><a parentName="strong" {...{
          "href": "https://redislabs.com/blog/redis-go-designed-improve-performance/"
        }}>{`Redis and Golang: Designed to Improve Performance`}</a></strong></p>
    <p><strong parentName="p"><a parentName="strong" {...{
          "href": "https://redislabs.com/docs/ultra-fast-recommendations-engine-using-redis-go/"
        }}>{`A High Performance Recommendation Engine with Redis and Go`}</a></strong></p>
    </MDXLayout>;
}

;
