export const toc = [{
  value: 'Step 1: Turn on Windows Subsystem for Linux',
  id: 'step-1-turn-on-windows-subsystem-for-linux',
  children: []
}, {
  value: 'Step 2: Launch Microsoft Windows Store',
  id: 'step-2-launch-microsoft-windows-store',
  children: []
