  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'index-redismod',
	title: 'Redis Modules in a Docker Container',
	sidebar_label: 'RedisMod',
	slug: '/explore/redismod'
};
export const metadata = {
  "unversionedId": "explore/redismod/index-redismod",
  "id": "explore/redismod/index-redismod",
  "isDocsHomePage": false,
  "title": "Redis Modules in a Docker Container",
  "description": "This simple container image bundles together the latest stable releases of Redis and select Redis modules from Redis Labs.",
  "source": "@site/docs/explore/redismod/index-redismod.mdx",
  "slug": "/explore/redismod",
  "permalink": "/explore/redismod",
