  /* @jsxRuntime classic */
/* @jsx mdx */
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
import useBaseUrl from '@docusaurus/useBaseUrl';
import RedisCard from '@site/src/theme/RedisCard';
export const toc = [{
  value: 'Modules included in the container',
  id: 'modules-included-in-the-container',
  children: []
}, {
  value: 'Step 1. Install Docker',
  id: 'step-1-install-docker',
  children: []
}, {
  value: 'Step 2. Running Redismod Docker container',
  id: 'step-2-running-redismod-docker-container',
  children: []
}, {
  value: 'Step 3. Connect to Redis database',
  id: 'step-3-connect-to-redis-database',
  children: []
}, {
  value: 'Step 4. Verify if all the Redis modules are getting loaded',
  id: 'step-4-verify-if-all-the-redis-modules-are-getting-loaded',
  children: []
}, {
  value: 'Step 5. Testing Redis Modules',
  id: 'step-5-testing-redis-modules',
  children: []
}, {
  value: 'References',
  id: 'references',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`This simple container image bundles together the latest stable releases of Redis and select Redis modules from Redis Labs.
This image is based on the official image of Redis from Docker. By default, the container starts with Redis' default configuration and all included modules loaded.`}</p>
    <h3 {...{
      "id": "modules-included-in-the-container"
    }}>{`Modules included in the container`}</h3>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://redislabs.com/modules/redis-search/"
        }}>{`RediSearch`}</a>{`: a full-featured search engine`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://redislabs.com/modules/redis-graph/"
        }}>{`RedisGraph`}</a>{`: a graph database`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://redislabs.com/modules/redis-timeseries/"
        }}>{`RedisTimeSeries`}</a>{`: a timeseries database`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://redislabs.com/modules/redis-ai/"
        }}>{`RedisAI`}</a>{`: a tensor and deep learning model server`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://redislabs.com/modules/redis-json/"
        }}>{`RedisJSON`}</a>{`: a native JSON data type`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://redislabs.com/modules/redis-bloom/"
        }}>{`RedisBloom`}</a>{`: native Bloom and Cuckoo Filter data types`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://redislabs.com/modules/redis-gears/"
        }}>{`RedisGears`}</a>{`: a dynamic execution framework`}</li>
    </ul>
    <div {...{
      "className": "admonition admonition-important alert alert--info"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M7 2.3c3.14 0 5.7 2.56 5.7 5.7s-2.56 5.7-5.7 5.7A5.71 5.71 0 0 1 1.3 8c0-3.14 2.56-5.7 5.7-5.7zM7 1C3.14 1 0 4.14 0 8s3.14 7 7 7 7-3.14 7-7-3.14-7-7-7zm1 3H6v5h2V4zm0 6H6v2h2v-2z"
              }}></path></svg></span>{`important`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`The latest redismod:preview Docker image holds RedisJSON 2.0 and RediSearch 2.2 Private Preview Release. This Private Preview Releases delivers real-time document capabilities with active-active support. The ability to index, query, and use full-text search on JSON documents using RediSearch is the coolest new feature of this release. Try it out today -`}</p><p parentName="div">{`docker run -p 6379:6379 redislabs/redismod:preview`}</p><p parentName="div">{`Refer to the link provided in the References section for further details.`}</p></div></div>
    <h3 {...{
      "id": "step-1-install-docker"
    }}>{`Step 1. Install Docker`}</h3>
    <p>{`To use RedisMod on a local Mac, the first step is to install Docker for your operating system.
Run the docker version command in a terminal window to make sure that docker is installed correctly.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{` docker version
`}</code></pre>
    <p>{`It should display Docker Engine Server and Client version successfully.`}</p>
    <h3 {...{
      "id": "step-2-running-redismod-docker-container"
    }}>{`Step 2. Running Redismod Docker container`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{` docker run -d -p 6379:6379 redislabs/redismod
`}</code></pre>
    <h3 {...{
      "id": "step-3-connect-to-redis-database"
    }}>{`Step 3. Connect to Redis database`}</h3>
    <p>{`You can either use `}<a parentName="p" {...{
        "href": "/create/homebrew"
      }}>{`redis-cli`}</a>{` or use `}<a parentName="p" {...{
        "href": "/explore/redisinsight/getting-started"
      }}>{`RedisInsight`}</a>{` to connect to Redis database.
Let's try using redis-cli as shown below:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{` redis-cli
`}</code></pre>
    <h3 {...{
      "id": "step-4-verify-if-all-the-redis-modules-are-getting-loaded"
    }}>{`Step 4. Verify if all the Redis modules are getting loaded`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{` $ redis-cli 
 127.0.0.1:6379> info modules
 # Modules
 module:name=rg,ver=10006,api=1,filters=0,usedby=[],using=[ai],options=[]
 module:name=ai,ver=10002,api=1,filters=0,usedby=[rg],using=[],options=[]
 module:name=timeseries,ver=10408,api=1,filters=0,usedby=[],using=[],options=[]
 module:name=bf,ver=20205,api=1,filters=0,usedby=[],using=[],options=[]
 module:name=graph,ver=20402,api=1,filters=0,usedby=[],using=[],options=[]
 module:name=ReJSON,ver=10007,api=1,filters=0,usedby=[],using=[],options=[]
 module:name=search,ver=20006,api=1,filters=0,usedby=[],using=[],options=[]
`}</code></pre>
    <h3 {...{
      "id": "step-5-testing-redis-modules"
    }}>{`Step 5. Testing Redis Modules`}</h3>
    <p>{`Let us test drive RediSearch modules as discussed below in detail.`}</p>
    <h4 {...{
      "id": "insert-data-into-redisearch"
    }}>{`Insert data into RediSearch`}</h4>
    <p>{`We are now ready to insert some data. This example uses movies data stored as Redis Hashes, so let’s insert a couple of movies:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`  HSET movies:11002 title "Star Wars: Episode V - The Empire Strikes Back" plot "Luke Skywalker begins Jedi training with Yoda." release_year 1980 genre "Action"   
 rating 8.7 votes 1127635
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{` HSET movies:11003 title "The Godfather" plot "The aging patriarch of an organized crime dynasty transfers control of his empire to his son." release_year 1972     
 genre "Drama" rating 9.2 votes 1563839 
`}</code></pre>
    <p>{`Your Redis database now contains two Hashes. It is simple to retrieve information using the HMGET command, if you know the key of the movies (movies:11002):`}</p>
    <pre><code parentName="pre" {...{}}>{` HMGET movies:11002 title rating
`}</code></pre>
    <h4 {...{
      "id": "create-an-index-in-redisearch"
    }}>{`Create an index in RediSearch`}</h4>
    <p>{`To be able to query the hashes on the field for title, say, or genre, you must first create an index. To create an index, you must define a schema to list the fields and their types that are indexed, and that you can use in your queries.`}</p>
    <p>{`Use the FT.CREATE command to create an index, as shown here:`}</p>
    <pre><code parentName="pre" {...{}}>{` FT.CREATE idx:movies ON hash PREFIX 1 "movies:" SCHEMA title TEXT SORTABLE release_year NUMERIC SORTABLE rating NUMERIC SORTABLE genre TAG SORTABLE
`}</code></pre>
    <h4 {...{
      "id": "search-the-movies-in-the-redisearch-index"
    }}>{`Search the movies in the RediSearch index`}</h4>
    <p>{`You can now use the FT.SEARCH to search your database, for example, to search all movies sorted by release year:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{` FT.SEARCH idx:movies * SORTBY release_year ASC RETURN 2 title release_year
`}</code></pre>
    <p>{`To test drive rest of Redis modules, please visit the links mentioned under "References" section.`}</p>
    <h3 {...{
      "id": "references"
    }}>{`References`}</h3>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://redislabs.com/blog/index-and-query-json-docs-with-redis/"
        }}>{`Indexing, Querying, and Full-Text Search of JSON Documents with Redis`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/RedisLabsModules/redismod"
        }}>{`Redismod GITHUB repository`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://developer.redislabs.com/explore/redisinsight/"
        }}>{`Connecting to the database using RedisInsight`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://developer.redislabs.com/howtos/redisjson"
        }}>{`RedisJSON Tutorial`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://developer.redislabs.com/howtos/redistimeseries"
        }}>{`RedisTimeSeries Tutorial`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://developer.redislabs.com/howtos/redisgraph"
        }}>{`RedisGraph Tutorial`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://developer.redislabs.com/howtos/redisbloom"
        }}>{`RedisBloom Tutorial`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://developer.redislabs.com/howtos/redisgears"
        }}>{`RedisGears Tutorial`}</a></li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
