/* @jsx mdx */
import RedisCard from '@site/src/theme/RedisCard';
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
import useBaseUrl from '@docusaurus/useBaseUrl';
export const toc = [{
  value: 'Prerequisites',
  id: 'prerequisites',
  children: []
}, {
  value: 'Let&#39;s Learn Together',
  id: 'lets-learn-together',
  children: []
}, {
  value: 'Course Contents',
  id: 'course-contents',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`This is a complete online course for Java/Spring developers wanting to learn how
Redis can serve as your primary database in Spring Applications and how to leverage the
power of the many Redis Modules in a traditional Spring Microservices application.`}</p>
    <p>{`In this course, you'll learn about building REST Services with Spring and Redis.
The course is delivered in a tutorial style, in which you can follow along and
build the course’s sample application.`}</p>
    <p>{`...And don’t worry if you get stuck during any lesson. The sample project provides
milestones in the form of Git branches that can help you pick up the project at any specific lesson.`}</p>
    <h3 {...{
      "id": "prerequisites"
    }}>{`Prerequisites`}</h3>
    <p>{`To get the most from this course, you'll need a machine that can run the application and the Redis server, which is provided as a Docker container.
You'll also need the following installed on your machine:`}</p>
    <ul>
      <li parentName="ul"><strong parentName="li">{`Git`}</strong>{`: See `}<a parentName="li" {...{
          "href": "https://git-scm.com/"
        }}>{`https://git-scm.com/`}</a></li>
      <li parentName="ul"><strong parentName="li">{`Java 11`}</strong>{` or higher: I recommend SDKMAN to install Java (`}<a parentName="li" {...{
          "href": "https://sdkman.io/"
        }}>{`https://sdkman.io/`}</a>{`)`}</li>
      <li parentName="ul"><strong parentName="li">{`Maven 3.2+`}</strong>{`: See `}<a parentName="li" {...{
          "href": "https://sdkman.io/sdks#maven"
        }}>{`https://sdkman.io/sdks#maven`}</a></li>
      <li parentName="ul"><strong parentName="li">{`Docker`}</strong>{`: See `}<a parentName="li" {...{
          "href": "https://docs.docker.com/get-docker/"
        }}>{`https://docs.docker.com/get-docker/`}</a></li>
      <li parentName="ul"><strong parentName="li">{`Docker Compose`}</strong>{`: See `}<a parentName="li" {...{
          "href": "https://docs.docker.com/compose/install/"
        }}>{`https://docs.docker.com/compose/install/`}</a></li>
      <li parentName="ul"><strong parentName="li">{`Postman or Curl`}</strong>{`: See `}<a parentName="li" {...{
          "href": "https://www.postman.com/"
        }}>{`https://www.postman.com/`}</a>{` / Curl is built-in on most platforms (for Windows see `}<a parentName="li" {...{
          "href": "https://curl.se/windows/"
        }}>{`https://curl.se/windows/`}</a>{`)`}</li>
      <li parentName="ul">{`Your favorite IDE or Code Editor (such as VSCode, Eclipse or IntelliJ IDEA)`}</li>
    </ul>
    <h3 {...{
      "id": "lets-learn-together"
    }}>{`Let's Learn Together`}</h3>
    <p>{`We're here to support your learning through a dedicated Discord channel that I'll be monitoring
along with other teaching assistants.  Join us on the Redis Discord server.`}</p>
    <h3 {...{
      "id": "course-contents"
    }}>{`Course Contents`}</h3>
    <div class="row">
 <div class="col">
     <RedisCard title="1. Spring and Redis: Up and Running" description="Create the skeleton for the course’s Spring Boot application." page="/develop/java/redis-and-spring-course/lesson_1" mdxType="RedisCard" />
  </div>
 <div class="col">
     <RedisCard title="2. Spring Data Redis" description="Introducing Spring Data Redis." page="/develop/java/redis-and-spring-course/lesson_2" mdxType="RedisCard" />
  </div>
 <div class="col">
     <RedisCard title="3. Object Mapping & Redis Repositories" description="Object Mapping & Redis Repositories." page="/develop/java/redis-and-spring-course/lesson_3" mdxType="RedisCard" />
  </div>
    </div>
    <div class="row">
 <div class="col">
     <RedisCard title="4. User/Roles & Secondary Indexes" description="User/Roles & Secondary Indexes." page="/develop/java/redis-and-spring-course/lesson_4" mdxType="RedisCard" />
  </div>
   <div class="col">
     <RedisCard title="5. Books, Categories & The Catalog" description="Books, Categories & The Catalog." page="/develop/java/redis-and-spring-course/lesson_5" mdxType="RedisCard" />
  </div>
   <div class="col">
     <RedisCard title="6. Domain Models with RedisJSON" description="Domain Models with RedisJSON." page="/develop/java/redis-and-spring-course/lesson_6" mdxType="RedisCard" />
  </div>
    </div>
    <div class="row">
 <div class="col">
     <RedisCard title="7. Search with RediSearch" description="Search with RediSearch." page="/develop/java/redis-and-spring-course/lesson_7" mdxType="RedisCard" />
  </div>
   <div class="col">
     <RedisCard title="8. Recommendations with RedisGraph" description="Recommendations with RedisGraph." page="/develop/java/redis-and-spring-course/lesson_8" mdxType="RedisCard" />
  </div>
   <div class="col">
     <RedisCard title="9. Caching REST Services with Redis" description="Caching REST Services with Redis." page="/develop/java/redis-and-spring-course/lesson_9" mdxType="RedisCard" />
  </div>
    </div>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
