  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'index-heroku',
	title: 'Create Redis database on Heroku',
	sidebar_label: 'Redis on Heroku',
	slug: '/create/heroku'
};
export const metadata = {
  "unversionedId": "create/heroku/index-heroku",
  "id": "create/heroku/index-heroku",
  "isDocsHomePage": false,
  "title": "Create Redis database on Heroku",
  "description": "Heroku is a cloud Platform as a Service (PaaS) supporting multiple programming languages that is used as a web application deployment model. Heroku lets the developer build, run and scale applications in a similar manner across all the languages(Java, Node.js, Scala, Clojure, Python, PHP, Ruby and Go).",
  "source": "@site/docs/create/heroku/index-heroku.mdx",
  "slug": "/create/heroku",
  "permalink": "/create/heroku",
  "editUrl": "https://github.com/redis-developer/redis-developer/edit/master/docs/create/heroku/index-heroku.mdx",
  "version": "current",
  "sidebar_label": "Redis on Heroku",
  "sidebar": "docs",
