  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'index-redisgraph',
	title: 'RedisGraph',
	sidebar_label: 'RedisGraph',
	slug: '/modules/redisgraph'
};
export const metadata = {
  "unversionedId": "modules/redisgraph/index-redisgraph",
  "id": "modules/redisgraph/index-redisgraph",
  "isDocsHomePage": false,
  "title": "RedisGraph",
  "description": "RedisGraph is a Redis module that enables enterprises to process any kind of connected data much faster than with traditional relational or existing graph databases. RedisGraph implements a unique data storage and processing solution (with sparse-adjacency matrices and GraphBLAS) to deliver the fastest and most efficient way to store, manage, and process connected data in graphs. With RedisGraph, you can process complex transactions 10 - 600 times faster than with traditional graph solutions while using 50 - 60% less memory resources than other graph databases!",
  "source": "@site/docs/modules/redisgraph/index-redisgraph.mdx",
  "slug": "/modules/redisgraph",
  "permalink": "/modules/redisgraph",
  "editUrl": "https://github.com/redis-developer/redis-developer/edit/master/docs/modules/redisgraph/index-redisgraph.mdx",
  "version": "current",
  "sidebar_label": "RedisGraph"
};
