export const toc = [{
  value: 'Reminder - Software Prerequisites',
  id: 'reminder---software-prerequisites',
  children: []
}, {
  value: 'Setup / Installation Process',
  id: 'setup--installation-process',
  children: [{
    value: 'Get the Code and Install Dependencies',
    id: 'get-the-code-and-install-dependencies',
    children: []
  }, {
    value: 'Start Redis (Docker)',
    id: 'start-redis-docker',
    children: []
  }, {
    value: 'Load the Sample Data into Redis',
    id: 'load-the-sample-data-into-redis',
    children: []
  }]
}, {
  value: 'Start and Configure RedisInsight',
  id: 'start-and-configure-redisinsight',
